import{_ as i,a as t}from"./chunks/web-command-bar.ae702e0c.js";import{_ as r,c as n,a as e,b as d,t as o,d as h,o as s}from"./app.86c455e9.js";const _=JSON.parse('{"title":"Keyboard Commands","description":"","frontmatter":{"title":"Keyboard Commands","editLink":true},"headers":[{"level":2,"title":"Library Keyboard Commands","slug":"library-keyboard-commands","link":"#library-keyboard-commands","children":[{"level":3,"title":"Adding Links with the keyboard","slug":"adding-links-with-the-keyboard","link":"#adding-links-with-the-keyboard","children":[]},{"level":3,"title":"Using the keyboard with search in the library","slug":"using-the-keyboard-with-search-in-the-library","link":"#using-the-keyboard-with-search-in-the-library","children":[]}]},{"level":2,"title":"Reader Keyboard Commands","slug":"reader-keyboard-commands","link":"#reader-keyboard-commands","children":[{"level":3,"title":"Creating highlights","slug":"creating-highlights","link":"#creating-highlights","children":[]},{"level":3,"title":"Changing Display Settings","slug":"changing-display-settings","link":"#changing-display-settings","children":[]},{"level":3,"title":"Article Actions","slug":"article-actions","link":"#article-actions","children":[]}]},{"level":2,"title":"Moving between views using the keyboard","slug":"moving-between-views-using-the-keyboard","link":"#moving-between-views-using-the-keyboard","children":[]},{"level":2,"title":"Accessing the Command bar","slug":"accessing-the-command-bar","link":"#accessing-the-command-bar","children":[]}],"relativePath":"using/keyboard.md"}'),l={name:"using/keyboard.md"},c={id:"frontmatter-title",tabindex:"-1"},g=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),b=h('<nav class="table-of-contents"><ul><li><a href="#library-keyboard-commands">Library Keyboard Commands</a><ul><li><a href="#adding-links-with-the-keyboard">Adding Links with the keyboard</a></li><li><a href="#using-the-keyboard-with-search-in-the-library">Using the keyboard with search in the library</a></li></ul></li><li><a href="#reader-keyboard-commands">Reader Keyboard Commands</a><ul><li><a href="#creating-highlights">Creating highlights</a></li><li><a href="#changing-display-settings">Changing Display Settings</a></li><li><a href="#article-actions">Article Actions</a></li></ul></li><li><a href="#moving-between-views-using-the-keyboard">Moving between views using the keyboard</a></li><li><a href="#accessing-the-command-bar">Accessing the Command bar</a></li></ul></nav><p>All major functionality in the Omnivore web client is available via the keyboard. There are two ways to use the keyboard within Omnivore: by opening the command bar and typing a command name or by using a keyboard command directly.</p><p>Currently, keyboard functionality is only available on the web.</p><p>You can view all available keyboard commands by tapping the <code>?</code> key.</p><p><img src="'+i+'" alt="List of keyboard commands"></p><h2 id="library-keyboard-commands" tabindex="-1">Library Keyboard Commands <a class="header-anchor" href="#library-keyboard-commands" aria-hidden="true">#</a></h2><p>Most library commands act upon the currently selected item. For example, to archive a library item, you would first select it and then tap <code>e</code> or use the command bar to archive the item.</p><p>To select items in the library use the arrow keys or the j and k keys. If your library is in grid mode the arrow keys allow navigating <code>up</code>/<code>down</code> and <code>left</code>/<code>right</code>. The <code>j</code> and <code>k</code> keys will navigate forward and backward.</p><ul><li><code>enter</code>: open the selected library item</li><li><code>o</code>: open the original version</li><li><code>l</code>: set labels on the selected item</li><li><code>e</code>: archive the selected item</li><li><code>r</code>: remove the item</li><li><code>shift</code> <code>i</code>: Mark as read</li><li><code>shift</code> <code>u</code>: Mark as unread</li></ul><h3 id="adding-links-with-the-keyboard" tabindex="-1">Adding Links with the keyboard <a class="header-anchor" href="#adding-links-with-the-keyboard" aria-hidden="true">#</a></h3><p>Tap the <code>a</code> key to open the Add Link dialog</p><h3 id="using-the-keyboard-with-search-in-the-library" tabindex="-1">Using the keyboard with search in the library <a class="header-anchor" href="#using-the-keyboard-with-search-in-the-library" aria-hidden="true">#</a></h3><p>Use the <code>/</code> key in the library view to start a search. To clear your current search tap the <code>x</code> key.</p><h2 id="reader-keyboard-commands" tabindex="-1">Reader Keyboard Commands <a class="header-anchor" href="#reader-keyboard-commands" aria-hidden="true">#</a></h2><p>Within the reader you can adjust your display settings, margins, line spacing, and theme using keyboard commands. You can also perform actions on items, like archiving and deleting from the reader view.</p><h3 id="creating-highlights" tabindex="-1">Creating highlights <a class="header-anchor" href="#creating-highlights" aria-hidden="true">#</a></h3><p>When you have text selected in the reader view, you can create a highlight by tapping <code>h</code>. If you&#39;d like to create a highlight with a note, tap <code>n</code>.</p><h3 id="changing-display-settings" tabindex="-1">Changing Display Settings <a class="header-anchor" href="#changing-display-settings" aria-hidden="true">#</a></h3><ul><li><code>d</code>: open the display settings modal</li></ul><h3 id="article-actions" tabindex="-1">Article Actions <a class="header-anchor" href="#article-actions" aria-hidden="true">#</a></h3><ul><li><code>e</code>: archive the current article</li><li><code>l</code>: set labels</li><li><code>o</code>: open the original version of the article</li></ul><h2 id="moving-between-views-using-the-keyboard" tabindex="-1">Moving between views using the keyboard <a class="header-anchor" href="#moving-between-views-using-the-keyboard" aria-hidden="true">#</a></h2><p>You can move between views in Omnivore using the keyboard. To read a library item first select it, and then use enter to open it in the reader view. When you finish reading, you can archive it and return to the library using ‘e’ or use the ‘u’ key to move “up” to the library.</p><h2 id="accessing-the-command-bar" tabindex="-1">Accessing the Command bar <a class="header-anchor" href="#accessing-the-command-bar" aria-hidden="true">#</a></h2><p>The command bar allows you to type commands. Open the command bar using CMD+K on MacOS, or CTRL+K on Windows.</p><p><img src="'+t+'" alt="Command Bar"></p>',26);function m(a,y,u,k,p,v){return s(),n("div",null,[e("h1",c,[d(o(a.$frontmatter.title)+" ",1),g]),b])}const C=r(l,[["render",m]]);export{_ as __pageData,C as default};
