import{_ as a,c as l,a as e,b as t,t as r,d as n,o}from"./app.8e8bab01.js";const w=JSON.parse('{"title":"Organizing with Labels","description":"","frontmatter":{"title":"Organizing with Labels","editLink":true},"headers":[{"level":2,"title":"Archiving","slug":"archiving","link":"#archiving","children":[]},{"level":2,"title":"Removing","slug":"removing","link":"#removing","children":[]},{"level":2,"title":"Labels","slug":"labels","link":"#labels","children":[]},{"level":2,"title":"Search","slug":"search","link":"#search","children":[]}],"relativePath":"using/organizing.md","lastUpdated":1700558139000}'),s={name:"using/organizing.md"},h={id:"frontmatter-title",tabindex:"-1"},c=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),d=n('<p>By default, the Library inbox displays all links you have saved. To manage your list and keep your reading organized, Omnivore provides the following actions:</p><nav class="table-of-contents"><ul><li><a href="#archiving">Archiving</a></li><li><a href="#removing">Removing</a></li><li><a href="#labels">Labels</a></li><li><a href="#search">Search</a></li></ul></nav><h2 id="archiving" tabindex="-1">Archiving <a class="header-anchor" href="#archiving" aria-hidden="true">#</a></h2><ol><li>Tap the Menu icon next to the link you wish to archive (on the mobile app, long press the link to open the menu, or swipe from the right).</li><li>Select Archive.</li><li>The link will disappear from the default Library view, but will show up if you select the Archived filter (see Filters below).</li></ol><h2 id="removing" tabindex="-1">Removing <a class="header-anchor" href="#removing" aria-hidden="true">#</a></h2><ol><li>Tap the Menu icon next to the link you wish to delete (on the mobile app, long press the link to open the menu, or swipe from the right).</li><li>Select Remove or Remove Item.</li><li>The link will disappear from the default Library view, but will show up if you select the Deleted filter (see Filters below) for the next 2 weeks.</li></ol><h2 id="labels" tabindex="-1">Labels <a class="header-anchor" href="#labels" aria-hidden="true">#</a></h2><ol><li>Tap the Menu icon next to any link and select Set Labels.</li><li>Select an existing label from the list or tap Edit Labels to create a new one.</li><li>The label will appear next to the link in your Library. Tap it to view all links with the same label.</li></ol><div class="tip custom-block"><p class="custom-block-title">Note: Omnivore will automatically assign some labels, such as “Newsletters.”</p></div><h2 id="search" tabindex="-1">Search <a class="header-anchor" href="#search" aria-hidden="true">#</a></h2><p>Omnivore uses allows full-text search of your entire library. To search through all your saved links, enter a keyword or phrase in the search bar.</p><p>You can combine keywords with labels and filters to focus your search even further using <a href="./search.html">advanced search</a>.</p>',12);function u(i,p,g,v,m,b){return o(),l("div",null,[e("h1",h,[t(r(i.$frontmatter.title)+" ",1),c]),d])}const _=a(s,[["render",u]]);export{w as __pageData,_ as default};
