import{_ as a}from"./chunks/web-rules-001.133e9351.js";import{_ as i,c as r,a as e,b as l,t as o,d as n,o as s}from"./app.08867612.js";const w=JSON.parse('{"title":"Rules","description":"","frontmatter":{"title":"Rules","editLink":true},"headers":[{"level":2,"title":"Introduction","slug":"introduction","link":"#introduction","children":[]},{"level":2,"title":"Creating Rules","slug":"creating-rules","link":"#creating-rules","children":[]},{"level":2,"title":"The Event Object","slug":"the-event-object","link":"#the-event-object","children":[]},{"level":2,"title":"Actions","slug":"actions","link":"#actions","children":[]},{"level":2,"title":"Some ideas for rules","slug":"some-ideas-for-rules","link":"#some-ideas-for-rules","children":[]}],"relativePath":"using/rules.md"}'),c={name:"using/rules.md"},d={id:"frontmatter-title",tabindex:"-1"},h=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),u=n('<nav class="table-of-contents"><ul><li><a href="#introduction">Introduction</a></li><li><a href="#creating-rules">Creating Rules</a></li><li><a href="#the-event-object">The Event Object</a></li><li><a href="#actions">Actions</a></li><li><a href="#some-ideas-for-rules">Some ideas for rules</a></li></ul></nav><h2 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-hidden="true">#</a></h2><div class="warning custom-block"><p class="custom-block-title">Rules are a beta feature and are currently only available on the web. Rules syntax might change in the future and break backwards compatibilty</p></div><p>Behind the scenes, Omnivore uses a rules engine to implement higher level features. Rules are applied when an article is created or modified.</p><p>A rule is a combination of a search filter, and a set of actions. When a page matches the search filter, the actions will be applied. For example:</p><ul><li><code>subscription:&quot;Money Talk&quot;</code> -&gt; <code>Add Label(&#39;Finance&#39;)</code>: This rule adds the Finance label to all pages that come from the <code>Money Talk</code> subscription.</li></ul><p><img src="'+a+'" alt="Screenshot of Rules"></p><h2 id="creating-rules" tabindex="-1">Creating Rules <a class="header-anchor" href="#creating-rules" aria-hidden="true">#</a></h2><p>Currently, rules can only be created on the <a href="https://omnivore.app/settings/rules" target="_blank" rel="noreferrer">/settings/rules</a> page. To create a new rule:</p><ul><li>Tap the <code>Create a new Rule</code> button on the top right</li><li>Enter a search filter. Use the <a href="./search.html">search</a> reference for more info</li><li>Select the actions you would like applied when the rule matches</li></ul><h2 id="the-event-object" tabindex="-1">The Event Object <a class="header-anchor" href="#the-event-object" aria-hidden="true">#</a></h2><p>Search filters for rules are slightly different than regular search filters. Along with all the regular <a href="./search.html">advanced search</a> functionality, rules have an event object. The event object allows you to create rules that match certain events.</p><ul><li><code>event.created</code>: The rule will only be matched when the page is first saved</li><li><code>event.updated</code>: The rule will be triggered when any part of a page&#39;s properties are modified. For example a label being added, the title being changed, or the read position being updated.</li></ul><h2 id="actions" tabindex="-1">Actions <a class="header-anchor" href="#actions" aria-hidden="true">#</a></h2><ul><li><code>Add Label</code>: Adds a set of labels to an article</li><li><code>Send Notification</code>: Sends a push notification to registered iOS devices</li><li><code>Archive</code>: Archives an article</li></ul><h2 id="some-ideas-for-rules" tabindex="-1">Some ideas for rules <a class="header-anchor" href="#some-ideas-for-rules" aria-hidden="true">#</a></h2><ul><li><code>is:read</code> -&gt; <code>Archive()</code>: Automatically archive an article once fully read</li><li><code>Text word</code> -&gt; <code>Add Label(&#39;some label&#39;)</code>: Automatically add a label to a page if it contains a specific word. For example <code>Vancouver Canucks</code> -&gt; <code>Add Label(&#39;Hockey&#39;)</code></li><li><code>event.created label:Newsletter</code> -&gt; <code>Send Notification()</code>: Send a push notification every time a newsletter issue is delivered.</li></ul>',17);function f(t,p,m,g,b,v){return s(),r("div",null,[e("h1",d,[l(o(t.$frontmatter.title)+" ",1),h]),u])}const T=i(c,[["render",f]]);export{w as __pageData,T as default};
