import{_ as a}from"./chunks/android-install-pwa.4c58f08e.js";import{_ as i,c as t,a as e,b as l,t as n,d as s,o}from"./app.8e8bab01.js";const k=JSON.parse('{"title":"安装","description":"","frontmatter":{"title":"安装","editLink":true},"headers":[{"level":2,"title":"浏览器扩展","slug":"浏览器扩展","link":"#浏览器扩展","children":[]},{"level":2,"title":"移动应用","slug":"移动应用","link":"#移动应用","children":[]},{"level":2,"title":"安装 Progressive Web App","slug":"安装-progressive-web-app","link":"#安装-progressive-web-app","children":[]}],"relativePath":"zh/using/install.md","lastUpdated":1676558672000}'),p={name:"zh/using/install.md"},d={id:"frontmatter-title",tabindex:"-1"},h=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),c=s('<nav class="table-of-contents"><ul><li><a href="#浏览器扩展">浏览器扩展</a></li><li><a href="#移动应用">移动应用</a></li><li><a href="#安装-progressive-web-app">安装 Progressive Web App</a></li></ul></nav><p>Omnivore 提供官方浏览器扩展和移动应用程序，使保存链接更容易。</p><h2 id="浏览器扩展" tabindex="-1">浏览器扩展 <a class="header-anchor" href="#浏览器扩展" aria-hidden="true">#</a></h2><p>浏览器扩展用于保存页面和 URL。安装扩展后，您可以点击 Omnivore 扩展按钮保存当前页面，或点击浏览器上下文菜单保存 URL 链接。有关保存项目的更多信息，请查看我们关于 <a href="./../../zh/using/saving.html">saving</a> 的文档。</p><ul><li><a href="https://omnivore.app/install/chrome" target="_blank" rel="noreferrer">在 Chrome 上安装</a></li><li><a href="https://omnivore.app/install/edge" target="_blank" rel="noreferrer">在 Edge 上安装</a></li><li><a href="https://omnivore.app/install/firefox" target="_blank" rel="noreferrer">在 Firefox 上安装</a></li><li><a href="https://omnivore.app/install/safari" target="_blank" rel="noreferrer">在 Safari 上安装</a></li></ul><h2 id="移动应用" tabindex="-1">移动应用 <a class="header-anchor" href="#移动应用" aria-hidden="true">#</a></h2><p>iOS 和 Android 应用程序用于保存链接、浏览库和阅读保存的内容。</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Android 应用处于预发布状态且功能有限。我们建议结合使用 Android 应用和 Android 上的 Progressive Web App，以获得最佳体验。</p></div><ul><li><a href="https://omnivore.app/install/ios" target="_blank" rel="noreferrer">在 iOS 上安装</a></li><li><a href="https://omnivore.app/install/android" target="_blank" rel="noreferrer">在 Android 上安装</a></li></ul><h2 id="安装-progressive-web-app" tabindex="-1">安装 Progressive Web App <a class="header-anchor" href="#安装-progressive-web-app" aria-hidden="true">#</a></h2><p>在 Android 上的 Chrome 中登录该应用程序后，您将在屏幕底部看到一个横幅，用于安装 Omnivore。这会将 Progressive Web App 安装到您的 Android 主屏幕。</p><p><img src="'+a+'" alt="Screenshot of Android PWA banner"></p>',12);function f(r,_,g,m,v,b){return o(),t("div",null,[e("h1",d,[l(n(r.$frontmatter.title)+" ",1),h]),c])}const S=i(p,[["render",f]]);export{k as __pageData,S as default};
