import{_ as d,a as i}from"./chunks/web-command-bar.ae702e0c.js";import{_ as l,c as o,a as e,b as c,t as r,d as t,o as h}from"./app.ebf30645.js";const S=JSON.parse('{"title":"快捷键","description":"","frontmatter":{"title":"快捷键","editLink":true},"headers":[{"level":2,"title":"快捷键文库","slug":"快捷键文库","link":"#快捷键文库","children":[{"level":3,"title":"使用键盘添加链接","slug":"使用键盘添加链接","link":"#使用键盘添加链接","children":[]},{"level":3,"title":"使用键盘在库中搜索","slug":"使用键盘在库中搜索","link":"#使用键盘在库中搜索","children":[]}]},{"level":2,"title":"阅读快捷键","slug":"阅读快捷键","link":"#阅读快捷键","children":[{"level":3,"title":"创建荧光笔高亮","slug":"创建荧光笔高亮","link":"#创建荧光笔高亮","children":[]},{"level":3,"title":"更改显示设置","slug":"更改显示设置","link":"#更改显示设置","children":[]},{"level":3,"title":"文章操作","slug":"文章操作","link":"#文章操作","children":[]}]},{"level":2,"title":"使用键盘在视图之间移动","slug":"使用键盘在视图之间移动","link":"#使用键盘在视图之间移动","children":[]},{"level":2,"title":"访问命令栏","slug":"访问命令栏","link":"#访问命令栏","children":[]}],"relativePath":"zh/using/keyboard.md","lastUpdated":1676734524000}'),n={name:"zh/using/keyboard.md"},s={id:"frontmatter-title",tabindex:"-1"},p=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),u=t('<nav class="table-of-contents"><ul><li><a href="#快捷键文库">快捷键文库</a><ul><li><a href="#使用键盘添加链接">使用键盘添加链接</a></li><li><a href="#使用键盘在库中搜索">使用键盘在库中搜索</a></li></ul></li><li><a href="#阅读快捷键">阅读快捷键</a><ul><li><a href="#创建荧光笔高亮">创建荧光笔高亮</a></li><li><a href="#更改显示设置">更改显示设置</a></li><li><a href="#文章操作">文章操作</a></li></ul></li><li><a href="#使用键盘在视图之间移动">使用键盘在视图之间移动</a></li><li><a href="#访问命令栏">访问命令栏</a></li></ul></nav><p>Omnivore Web 客户端中的所有主要功能都可以通过键盘获得。有两种方法可以在 Omnivore 中使用键盘：打开命令栏并键入命令名称或直接使用快捷键。</p><p>目前，键盘功能仅在 Web 上可用。</p><p>您可以通过点击 <code>?</code> 键查看所有可用的快捷键。</p><p><img src="'+d+'" alt="List of keyboard commands"></p><h2 id="快捷键文库" tabindex="-1">快捷键文库 <a class="header-anchor" href="#快捷键文库" aria-hidden="true">#</a></h2><p>大多数命令文库作用于当前选定的文章。例如，要存档文库的文章，请先选择它，然后点击 <code>e</code> 或使用命令栏存档该文章。</p><p>要选择文库中的文章，请使用箭头键或 j 和 k 键。如果您的文库处于网格模式，箭头键允许导航 <code>up</code>/<code>down</code> 和 <code>left</code>/<code>right</code>。 <code>j</code> 和 <code>k</code> 键将向前和向后导航。</p><ul><li><code>enter</code>: 打开选定的文库中的文章</li><li><code>o</code>: 打开原始版本</li><li><code>l</code>: 为所选文章设置标签</li><li><code>e</code>: 存档所选文章</li><li><code>r</code>: 删除文章</li><li><code>shift</code> <code>i</code>: 标记为已读</li><li><code>shift</code> <code>u</code>: 标记为未读</li></ul><h3 id="使用键盘添加链接" tabindex="-1">使用键盘添加链接 <a class="header-anchor" href="#使用键盘添加链接" aria-hidden="true">#</a></h3><p>点击 <code>a</code> 键打开“添加链接”对话框</p><h3 id="使用键盘在库中搜索" tabindex="-1">使用键盘在库中搜索 <a class="header-anchor" href="#使用键盘在库中搜索" aria-hidden="true">#</a></h3><p>使用文库视图中的 <code>/</code> 键开始搜索。要清除当前搜索，请点击 <code>x</code> 键。</p><h2 id="阅读快捷键" tabindex="-1">阅读快捷键 <a class="header-anchor" href="#阅读快捷键" aria-hidden="true">#</a></h2><p>在阅读器中，您可以使用快捷键调整显示设置、边距、行距和主题。您还可以对文章执行操作，例如从阅读器视图中存档和删除。</p><h3 id="创建荧光笔高亮" tabindex="-1">创建荧光笔高亮 <a class="header-anchor" href="#创建荧光笔高亮" aria-hidden="true">#</a></h3><p>在阅读器视图中选择文本后，可以通过点击 <code>h</code> 来创建荧光笔高亮显示。如果您想创建带有注释的高亮显示，请点按 <code>n</code>.</p><h3 id="更改显示设置" tabindex="-1">更改显示设置 <a class="header-anchor" href="#更改显示设置" aria-hidden="true">#</a></h3><ul><li><code>d</code>: 打开“显示设置”模式</li></ul><h3 id="文章操作" tabindex="-1">文章操作 <a class="header-anchor" href="#文章操作" aria-hidden="true">#</a></h3><ul><li><code>e</code>: 当前文章存档</li><li><code>l</code>: 设置标签</li><li><code>o</code>: 打开文章的原始版本</li></ul><h2 id="使用键盘在视图之间移动" tabindex="-1">使用键盘在视图之间移动 <a class="header-anchor" href="#使用键盘在视图之间移动" aria-hidden="true">#</a></h2><p>您可以使用键盘在 Omnivore 中的视图之间移动。若要阅读文库的文章，请先选择它，然后使用 enter 在阅读器视图中将其打开。阅读完毕后，您可以将其存档并使用 <code>e</code> 返回文库，或使用 <code>u</code> 键将向 <code>up</code> 移动到文库。</p><h2 id="访问命令栏" tabindex="-1">访问命令栏 <a class="header-anchor" href="#访问命令栏" aria-hidden="true">#</a></h2><p>命令栏允许您键入命令。在 MacOS 上使用 <code>Cmd+K</code> 或在 Windows 上使用 <code>Ctrl+K</code> 打开命令栏。</p><p><img src="'+i+'" alt="Command Bar"></p>',26);function f(a,_,m,b,k,g){return h(),o("div",null,[e("h1",s,[c(r(a.$frontmatter.title)+" ",1),p]),u])}const T=l(n,[["render",f]]);export{S as __pageData,T as default};
