import{_ as l}from"./chunks/web-rules-001.133e9351.js";import{_ as i,c as t,a as e,b as d,t as r,d as o,o as c}from"./app.86c455e9.js";const x=JSON.parse('{"title":"Rules","description":"","frontmatter":{"title":"Rules","editLink":true},"headers":[{"level":2,"title":"简介","slug":"简介","link":"#简介","children":[]},{"level":2,"title":"创建规则","slug":"创建规则","link":"#创建规则","children":[]},{"level":2,"title":"事件对象","slug":"事件对象","link":"#事件对象","children":[]},{"level":2,"title":"操作","slug":"操作","link":"#操作","children":[]},{"level":2,"title":"规则的一些想法","slug":"规则的一些想法","link":"#规则的一些想法","children":[]}],"relativePath":"zh/using/rules.md"}'),n={name:"zh/using/rules.md"},s={id:"frontmatter-title",tabindex:"-1"},h=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),u=o('<nav class="table-of-contents"><ul><li><a href="#简介">简介</a></li><li><a href="#创建规则">创建规则</a></li><li><a href="#事件对象">事件对象</a></li><li><a href="#操作">操作</a></li><li><a href="#规则的一些想法">规则的一些想法</a></li></ul></nav><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-hidden="true">#</a></h2><p>::: 警告：规则是一项测试功能，目前仅在 Web 上可用。规则语法将来可能会更改并破坏或向上兼容性。 :::</p><p>在幕后，Omnivore 使用规则引擎来实现更高级别的功能。创建或修改文章时应用规则。</p><p>规则是搜索筛选器和一组操作的组合。当页面与搜索筛选器匹配时，将应用操作。例如：</p><ul><li><code>subscription:&quot;Money Talk&quot;</code> -&gt; <code>Add Label(&#39;Finance&#39;)</code>: 此规则将 “ Finance ” 标签添加到来自 <code>Money Talk</code> 订阅的所有页面。</li></ul><p><img src="'+l+'" alt="Screenshot of Rules"></p><h2 id="创建规则" tabindex="-1">创建规则 <a class="header-anchor" href="#创建规则" aria-hidden="true">#</a></h2><p>目前只能在 <a href="https://omnivore.app/settings/rules" target="_blank" rel="noreferrer">/settings/rules</a> <sup>(/设置/规则) </sup>页面上创建规则。如果要创建新规则：</p><ul><li>点击右上角的 <code>Create a new Rule</code> <sup>(创建新规则) </sup>按钮。</li><li>输入搜索筛选器。使用 <a href="./../../zh/using/search.html">搜索</a> 参考获取更多信息。</li><li>选择要在规则匹配时应用的操作。</li></ul><h2 id="事件对象" tabindex="-1">事件对象 <a class="header-anchor" href="#事件对象" aria-hidden="true">#</a></h2><p>规则的搜索筛选器与常规搜索筛选器略有不同。除了所有常规的 <a href="./../../zh/using/search.html">高级搜索</a> 功能外，规则还有一个事件对象。事件对象允许您创建与特定事件匹配的规则。</p><ul><li><code>event.created</code>: 该规则仅在首次保存页面时匹配。</li><li><code>event.updated</code>: 当修改页面属性的任何部分时，将触发该规则。例如，正在添加标签、更改标题或更新阅读位置。</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-hidden="true">#</a></h2><ul><li><code>Add Label</code>: 向文章添加一组标签</li><li><code>Send Notification</code>: 向已注册的iOS设备发送推送通知。</li><li><code>Archive</code>: 存档中的文章。</li></ul><h2 id="规则的一些想法" tabindex="-1">规则的一些想法 <a class="header-anchor" href="#规则的一些想法" aria-hidden="true">#</a></h2><ul><li><code>is:read</code> -&gt; <code>Archive()</code>: 完全阅读完后自动将文章移至存档。</li><li><code>Text word</code> -&gt; <code>Add Label(&#39;some label&#39;)</code>: 如果页面包含特定字词，则自动向页面添加标签。例如： <code>Vancouver Canucks</code> -&gt; <code>Add Label(&#39;Hockey&#39;)</code></li><li><code>event.created label:Newsletter</code> -&gt; <code>Send Notification()</code>: 每次发送新闻稿件问题时发送推送通知。</li></ul>',17);function p(a,_,f,g,m,b){return c(),t("div",null,[e("h1",s,[d(r(a.$frontmatter.title)+" ",1),h]),u])}const S=i(n,[["render",p]]);export{x as __pageData,S as default};
